import React, { useEffect, useState } from "react";
import { store, persistor } from "app/store";
import { Provider } from "react-redux";
import { PersistGate } from "redux-persist/integration/react";
import Navigator from "./navigation";
import { Text } from '@components'
import axios from 'axios';

console.disableYellowBox = true;

export default function App() {
  const [posts, setPost] = useState([]);

  useEffect(() => {
    let mounted = true;
    if (mounted) {
      axios
        .get('http://dkanh.com/wp-json/wp/v2/posts')
        .then((res) => {
          setPost(res.data);
        })
        .catch((err) => {
          console.log(err);
        });
    }
    return () => (mounted = false);
  }, []);


  console.log(posts);
  return (
    <Provider store={store}>
      <PersistGate loading={null} persistor={persistor}>
      {posts.length > 0 ? (
						posts.map((post) => {
							if (post === undefined) {
								return '';
							}
							return (
								<Text className={`col-md-4 mb-4`} key={post.id}>
									<Text class="card">
										<Link className="card-body" to={`/post/${post.id}`}>
											<h5 className="card-title">
												<Text dangerouslySetInnerHTML={{ __html: post.title.rendered }} />{' '}
											</h5>

											<p>
												<Text dangerouslySetInnerHTML={{ __html: post.content.rendered.substring(0, 40) }} />
											</p>
										</Link>
									</Text>
								</Text>
							);
						})
					) : (
						<Text>Loading...</Text>
					)}
        <Navigator />
      </PersistGate>
    </Provider>
  );
}
